import React, { useEffect, useState } from 'react';
import api from '../../services/api';

function TravelerProfile() {
  const [profile, setProfile] = useState({
    name: '',
    email: '',
    imageUrl: '',
  });
  const [newImage, setNewImage] = useState(null);

  // Fetch traveler profile on mount
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await api.get('/traveler/profile');
        setProfile(res.data);
      } catch (err) {
        alert('Failed to load profile.');
      }
    };
    fetchProfile();
  }, []);

  // Handle profile update
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await api.put('/traveler/profile', {
        name: profile.name,
        email: profile.email,
      });
      alert('Profile updated successfully.');
    } catch (err) {
      alert('Update failed.');
    }
  };

  // Handle image upload
  const handleImageUpload = async () => {
    if (!newImage) return;
    const formData = new FormData();
    formData.append('image', newImage);
    try {
      const res = await api.post('/traveler/upload-image', formData);
      setProfile((prev) => ({ ...prev, imageUrl: res.data.imageUrl }));
      alert('Image uploaded.');
    } catch (err) {
      alert('Image upload failed.');
    }
  };

  return (
    <main className="container mt-5">
      <h2 className="text-center mb-4">Traveler Profile</h2>
      <div className="row justify-content-center">
        <div className="col-md-6">
          {/* Profile Image */}
          <div className="text-center mb-4">
            <img
              src={profile.imageUrl || 'https://via.placeholder.com/150'}
              alt="Traveler profile"
              className="rounded-circle"
              width="150"
              height="150"
            />
          </div>

          {/* Profile Form */}
          <form onSubmit={handleUpdate} aria-label="Traveler profile form">
            <div className="mb-3">
              <label htmlFor="name" className="form-label">Full Name</label>
              <input
                type="text"
                id="name"
                className="form-control"
                value={profile.name}
                onChange={(e) => setProfile({ ...profile, name: e.target.value })}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="email" className="form-label">Email Address</label>
              <input
                type="email"
                id="email"
                className="form-control"
                value={profile.email}
                onChange={(e) => setProfile({ ...profile, email: e.target.value })}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary w-100">Update Profile</button>
          </form>

          {/* Image Upload */}
          <div className="mt-4">
            <label htmlFor="imageUpload" className="form-label">Upload Profile Image</label>
            <input
              type="file"
              id="imageUpload"
              className="form-control"
              accept="image/*"
              onChange={(e) => setNewImage(e.target.files[0])}
            />
            <button
              className="btn btn-outline-secondary w-100 mt-2"
              onClick={handleImageUpload}
            >
              Upload Image
            </button>
          </div>
        </div>
      </div>
    </main>
  );
}

export default TravelerProfile;
